mapper.select:
select

from lesson
where course_id = #{courseId,jdbcType=INTEGER}
and lesson_id = #{lessonId,jdbcType=INTEGER}

mapper.delete:
delete from lesson
where course_id = #{courseId,jdbcType=INTEGER}
  and lesson_id = #{lessonId,jdbcType=INTEGER}

mapper.insert:
insert into lesson (course_id, lesson_id, lesson_name
  )
values (#{courseId,jdbcType=INTEGER}, #{lessonId,jdbcType=INTEGER}, #{lessonName,jdbcType=VARCHAR}
  )

mapper.update:
update lesson


        lesson_name = #{lessonName,jdbcType=VARCHAR},


where course_id = #{courseId,jdbcType=INTEGER}
and lesson_id = #{lessonId,jdbcType=INTEGER}

update lesson
set lesson_name = #{lessonName,jdbcType=VARCHAR}
where course_id = #{courseId,jdbcType=INTEGER}
  and lesson_id = #{lessonId,jdbcType=INTEGER}
