mapper.select:
select m.msg_id,u.user_id as authorId, u.nick_name authorName, m.post_date,m.content
from msg m inner join user u
on m.author_id=u.user_id
where m.to_id=#{userId,jdbcType=INTEGER}
and m.status=#{status,jdbcType=TINYINT}

mapper.update:
update msg
set msg.status=1
where msg_id=#{parentId,jdbcType=INTEGER}
