mapper.insert:
insert into workSubmit (submit_id, user_id, course_id, 
  submit_status, work_id, content
  )
values (#{submitId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{courseId,jdbcType=INTEGER}, 
  #{submitStatus,jdbcType=TINYINT}, #{workId,jdbcType=INTEGER}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.update:
update workSubmit
set user_id = #{userId,jdbcType=INTEGER},
  course_id = #{courseId,jdbcType=INTEGER},
  submit_status = #{submitStatus,jdbcType=TINYINT},
  work_id = #{workId,jdbcType=INTEGER}
where submit_id = #{submitId,jdbcType=INTEGER}

update workSubmit
set
submit_status=#{status,jdbcType=TINYINT},
score=#{score,jdbcType=INTEGER}
where submit_id=#{submitId,jdbcType=INTEGER}

mapper.select:
select ws.score
from workSubmit ws
where ws.work_id=#{workId,jdbcType=INTEGER} and ws.user_id=#{userId,jdbcType=INTEGER}
